package day2;

public class Operations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=2;
		System.out.println(a+b);
		System.out.println(a==b);
		System.out.println(a>0&&b>0);
		int c=a*b;
		System.out.println(c);
		System.out.println(c++);//unary operator
		

	}

}
